import * as actionTypes from '../actions/actionTypes'

const initialState = {
    pokemons: [],
    lowerBound: 0,
    upperBound: 48,
}
const fetchPokemon = (state, action) => {
    console.log(action.pokemon)
    return {
        ...state,
       pokemons: action.pokemon, 
    }
}
const reducer = (state = initialState, action) =>{
    switch(action.type){
        case actionTypes.FETCH_POKEMON_SUCCESS: fetchPokemon(state, action)
        case actionTypes.FETCH_POKEMON_FAILED: return {}
            break
        default:
            return state


    }
}
export default reducer
