import * as actions from './actionTypes'
import axios from '../../axios'
export const fetchPokemon = () => {
    return dispatch => {
        axios.get('/?offset='+state.lowerBound+'&limit='+state.upperBound).then(response=>{ //maybe get the page number and add it to the url
            console.log(response.data)
            return({
                ...state,
                pokemons: response.data.results
            })
        }).catch(error => {
            console.log(error)
        })
    }
}

export const fetchPokemonSuccess = () => {
    return {
        type : actions.GET_POKEMON,
        range: 48
    }
}
