import React, { Component } from "react";
import classes from "./Pokemon.module.css";
import { connect } from "react-redux";
import * as actions from "../../store/actions/index";
import Spinner from "../UI/Spinner/Spinner";
import PokemonType from "./PokemonType/PokemonType";
import PokemonStats from "./PokemonStats/PokemonStats";
import PokemonAbilities from "./PokemonAbilities/PokemonAbilities";
class Pokemon extends Component {
  componentDidMount() {
    this.props.onLoadPokemon(this.props.match.params.id);
  }

  render() {
    let card = <Spinner />;
    if (!this.props.loading && this.props.pkm !== undefined) {
      card = (
        <div className={classes.Card}>
          <div className={classes.CardHeader}>
            <div className={classes.Row}>
              <div className={classes.Col2}>
                <div className={classes.Id}>
                  <h5>{this.props.pkm.id}</h5>
                </div>
              </div>
              <PokemonType types={this.props.pkm.types} />
            </div>
          </div>
          <div className="card-body">
            <div className="row align-items-center">
              <div className="col-md-3">
                <img
                  src={this.props.pkm.imageURL}
                  className="card-img-top rounded mx-auto mt-2"
                  alt=""
                />
              </div>
              <div className="col-md-9">
                <h4 className="mx-auto">
                  {this.props.pkm.name
                    .toLowerCase()
                    .split(" ")
                    .map(
                      (letter) =>
                        letter.charAt(0).toUpperCase() + letter.substring(1)
                    )}
                </h4>
                <PokemonStats stats={this.props.pkm.stats} />
              </div>
            </div>
            <div className="row align-items-center">
              <div className="col-12">
                <div className="row align-items-center">
                  <div className="col-6 ">
                    <br />
                    <strong>Attributes</strong>
                    <p>Height: {this.props.pkm.height}</p>
                    <p>Weight: {this.props.pkm.weight}</p>
                  </div>
                  <PokemonAbilities
                    abilities={this.props.pkm.abilities}
                    base_experience={this.props.pkm.base_experience}
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
      );
    }
    return <div className={classes.Pokemon}>{card}</div>;
  }
}
const mapStateToProps = (state) => {
  return {
    pkm: state.pokemon.pokemon,
    loading: state.pokemon.loading,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    onLoadPokemon: (id) => dispatch(actions.fetchPokemonById(id)),
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(Pokemon);
