import * as actions from './actionTypes'
import axios from '../../axios'

export const fetchPokemon = (lowerBound, upperBound) => {
    return (dispatch) => {
        axios.get('/?offset='+lowerBound+'&limit='+upperBound).then(response=>{ //maybe get the page number and add it to the url
            dispatch(fetchPokemonSuccess(response.data.results))
        }).catch(error => {
            console.log(error)
            dispatch(fetchPokemonFailed())
        })
    }
}
export const fetchPokemonFailed = () => {
    return {
        type :  actions.FETCH_POKEMON_FAILED
    }
}
export const fetchPokemonSuccess = (pokemonArray) => {
    return {
        type : actions.FETCH_POKEMON_SUCCESS,
        pokemon : pokemonArray
    }
}

export const nextPokemonPage = (lower, upper) => {
    const lowerBound = lower + 29;
    const upperBound = upper + 29;
    return fetchPokemon(lowerBound, upperBound)
}

export const previousPokemonPage = () => {
    return {
        type : actions.PREVIOUS_POKEMON_PAGE,
    }
}
