import * as actionTypes from '../actions/actionTypes'
import axios from '../../axios-object'
const initialState = {
    pokemons: [],
    lowerBound: 0,
    upperBound: 48,
}
const reducer = (state = initialState, action) =>{
    switch(action.type){
        case actionTypes.FETCH_POKEMON_SUCCESS:
            axios.get('/?offset='+state.lowerBound+'&limit='+state.upperBound).then(response=>{ //maybe get the page number and add it to the url
                console.log(response.data)
                return({
                    ...state,
                    pokemons: response.data.results
                })
            }).catch(error => {
                console.log(error)
            })
            break
        default:
            return state


    }
}
export default reducer
