import React from "react";

const PokemonName = (props) => {
  return (
    <h4 className="mx-auto">
      {props.name
        .toLowerCase()
        .split(" ")
        .map((letter) => letter.charAt(0).toUpperCase() + letter.substring(1))}
    </h4>
  );
};
