import * as actionTypes from '../actions/actionTypes'
import axios from '../../axios-object'
const initialState = {
    pokemons: []
}
const reducer = (state = initialState, action) =>{
    switch(action.type){
        case actionTypes.GET_POKEMON:
            axios.get('/?offset=0&limit=100').then(response=>{ //maybe get the page number and add it to the url
                console.log(response.data)
                return({
                    ...state,
                    pokemons: response.data
                })
            }).catch(error => {
                console.log(error)
            })
            break
        default:
            return state


    }
}
export default reducer
