import * as actionTypes from '../actions/actionTypes'
import { updateObject } from '../../shared/utility'
const initialState = {
    pokemons: [],
    lowerBound: 0,
    upperBound: 28,
}
const fetchPokemon = (state, action) => {
    return {
        pokemons: action.pokemon
    }
}
const nextPage = (state , action) => {
    const newPage = {
        lowerBound : state.lowerBound + 29,
        newUpper : state.upperBound + 29
    }
    return updateObject(state, newPage)
}
const previousPage = (state , action) => {
    const newLower = state.lowerBound - 29;
    const newUpper = state.upperBound - 29;
    return updateObject(state, {lowerBound:newLower, upperBound: newUpper})
}

const reducer = (state = initialState, action) =>{
    switch(action.type){
        case actionTypes.FETCH_POKEMON_SUCCESS: return fetchPokemon(state, action)
        case actionTypes.FETCH_POKEMON_FAILED: return {}
        case actionTypes.NEXT_POKEMON_PAGE: return nextPage(state, action)
        case actionTypes.PREVIOUS_POKEMON_PAGE: return previousPage(state, action)
        default: return state


    }
}
export default reducer
