import React, {Component} from "react"
import PokemonListCard from '../../components/PokemonListCard/PokemonListCard'
import PageManager from '../../components/PageManager/PageManager'
import { connect } from 'react-redux'
import axios from '../../axios'
import * as actionTypes from '../../store/actions/actionTypes'
import PokeCard from '../PokeCard/PokeCard'
import Modal from '../../components/Modal/Modal'
import Backdrop from '../../components/Backdrop/Backdrop'

class Layout extends Component{
    state = {
        pokemons: [],
        loading: true,
        displaymodal: false,
    }

    componentDidMount(){
        axios.get('/?offset=0&limit=48').then(response=>{ //maybe get the page number and add it to the url
            this.setState({
                pokemons: response.data.results,
                loading: false
            })
            console.log(this.state.pokemons)
        }).catch(error => {

        })
    }

    pokemonSelectedHandler(name){
        this.props.history.push({pathname: '/pokemon/' + name})
    }

    onViewModal = () =>{
        this.setState({displayModal: true})
    }
    onRemoveModal = () =>{
        this.setState({displayModal: false})
    }

    render(){
        let PokemonList = null
        if(this.state.pokemons){
            PokemonList = this.state.pokemons.map((pokemon, key) =>{
                return <PokeCard
                url={pokemon.url}
                pokemon={pokemon}
                key={pokemon.name}
                pokemonSelect={()=>this.pokemonSelectedHandler(pokemon.name)}
                showModal={this.onViewModal}
                />
            })
        }
        return (
            <div>
                <div className="row container-fluid mx-auto">  
                    {PokemonList}       
                </div>  
                <PageManager />
                {this.state.displayModal ? <Modal closed={this.onRemoveModal} show={this.state.displayModal} /> : null}
                {this.state.displayModal ? <Backdrop show={this.state.displayModal} /> : null}
            </div>
        )
    }


}

const mapStateToProp = state =>{
    return{
        pkm : state.pokemon,
    }
}
const mapDispatchToProps = dispatch =>{
    return{
        getPokemon: (lower, upper) => dispatch({type: actionTypes.GET_POKEMON, lowerBound: lower, upperBound: upper}),
    }
}

export default connect(mapStateToProp,mapDispatchToProps)(Layout);
