import * as actionTypes from '../actions'
import axios from 'axios'
const initialState = {
    pokemons: []
}
const reducer = (state = initialState, action) =>{
    switch(action.type){
        case actionTypes.GET_POKEMON:
            axios.get('https://pokeapi.co/api/v2/pokemon/?offset=0&limit=100').then(response=>{ //maybe get the page number and add it to the url
                return({
                    ...state,
                    pokemons: response.data
                })
            }).catch(error => {
                console.log(error)
            })

    }
}
export default reducer
