import { put } from "redux-saga/effects";
import * as actions from "../actions/index";
import axios from "../../axios/auth";

export function* checkAuthTimeoutSaga(action) {
  yield setTimeout(() => {
    put(actions.logout());
  }, action.expirationTime * 1000);
}
