import * as actionTypes from '../actions/actionTypes'
import { updateObject } from '../../shared/utility'
const initialState = {
    pokemons: [],
    lowerBound: 0,
    upperBound: 48,
}
const fetchPokemon = (state, action) => {
    console.log(action.pokemon)
    const updatedObject = {
        pokemon: action.pokemon
    }
    return updateObject(state, updatedObject)
}
const reducer = (state = initialState, action) =>{
    switch(action.type){
        case actionTypes.FETCH_POKEMON_SUCCESS: fetchPokemon(state, action)
        case actionTypes.FETCH_POKEMON_FAILED: return {}
            break
        default:
            return state


    }
}
export default reducer
