import * as actionTypes from '../actions/actionTypes'
import {updateObject} from '../../shared/utility'
const initialState = {
    pokemons: [],
    offset: 0,
    limit: 28,
    pokemon: {},
}
const fetchPokemon = (state, action) => {
    return {
        pokemons: action.pokemon,
        offset: action.lower,
        upperBound: action.upper,
    }
}
const updatePokemonById = (state, action) => {
    console.log(action.pokemon)
    return updateObject(state, {pokemon: action.pokemon})
}

const reducer = (state = initialState, action) =>{
    switch(action.type){
        case actionTypes.FETCH_POKEMON_SUCCESS: return fetchPokemon(state, action)
        case actionTypes.FETCH_POKEMON_FAILED: return {}
        case actionTypes.FETCH_POKEMON_BY_ID_SUCCESS: return updatePokemonById(state,action)
        default: return state


    }
}
export default reducer
