import * as actionTypes from '../actions/actionTypes'
const initialState = {
    pokemons: [],
    lowerBound: 0,
    upperBound: 28,
}
const fetchPokemon = (state, action) => {
    return {
        ...state,
        pokemons: action.pokemon,
        lowerBound: action.lower,
        upperBound: action.upper,
    }
}

const reducer = (state = initialState, action) =>{
    switch(action.type){
        case actionTypes.FETCH_POKEMON_SUCCESS: return fetchPokemon(state, action)
        case actionTypes.FETCH_POKEMON_FAILED: return {}
        default: return state


    }
}
export default reducer
