import React, {Component} from "react"
import axios from 'axios'
import PokeCard from '../PokeCard/PokeCard.js'
import { connect } from 'react-redux'
import * as actionTypes from '../../store/actions'

class Layout extends Component{
    state = {
        view: true,
        pokemons: [],

    }
    componentDidMount(){
        axios.get('https://pokeapi.co/api/v2/pokemon/?offset=0&limit=100').then(response=>{ //maybe get the page number and add it to the url
            this.setState({
                pokemons: response.data.results,
            })
        }).catch(error => {
            console.log(error)
        })
    }

    pokemonSelectedHandler(name){
        this.props.history.push({pathname: '/pokemon/' + name})
    }

    render(){
        let PokemonList = null
        if(this.state.view){
            PokemonList = this.state.pokemons.map((pokemon, key) =>{
                return <PokeCard
                url={pokemon.url}
                pokemon={pokemon}
                key={pokemon.name}
                clicked={()=>this.pokemonSelectedHandler(pokemon.name)}
                />
            })
        }
        return (
            <div className="App bg-dark">
                <div className="row container-fluid mx-auto">  
                    {PokemonList}       
                </div>  
                <p style={{color: 'white', marginLeft: '40px'}}>Created By: Luis Preciado</p>
            </div>
        )
    }


}

const mapStateToProp = state =>{
    return{
        pkm : state.pokemon,
    }
}
const mapDispatchToProps = dispatch =>{
    return{
        getPokemon: (lower, upper) => dispatch({type: actionTypes.GET_POKEMON, lowerBound: lower, upperBound: upper}),
    }
}

export default connect(mapStateToProp,mapDispatchToProps)(Layout);
